package bll;import bll.validators.EmailValidator;import bll.validators.Validator;import dao.ClientDAO;import model.Client;import javax.swing.*;import java.util.ArrayList;import java.util.List;public class ClientBLL {    private ClientDAO clientDAO = new ClientDAO();    private List<Validator<Client>> validators = new ArrayList<>();    public ClientBLL() {        validators.add(new EmailValidator());    }    public void add(Client client) {        try {            for (Validator<Client> validator : validators) {                validator.validate(client);            }            clientDAO.insert(client, 1);        } catch (Exception e) {            JOptionPane.showMessageDialog(null, "Eroare la adăugarea clientului: adresa de email introdusă nu este corectă.");        }    }    public Client find(int id) {        return clientDAO.findById(id);    }    public void delete(int id) {        try {            clientDAO.delete(id);        } catch (Exception e) {            JOptionPane.showMessageDialog(null, "Eroare la ștergerea clientului.");        }    }    public void updateClient(int id, Client client) {        try {            clientDAO.updateCustomerById(id, client.getName(), client.getEmail(), client.getPhoneNumber(), client.getAddress());        } catch (Exception e) {            JOptionPane.showMessageDialog(null, "Eroare la actualizarea clientului.");        }    }    public JTable displayClients() {        ArrayList<Client> clients = clientDAO.viewAll();        JTable table = clientDAO.viewAll2(clients);        return table;    }}