package bll;import bll.validators.NrValidator;import bll.validators.Validator;import dao.ProductDAO;import model.Product;import javax.swing.*;import java.util.ArrayList;import java.util.List;public class ProductBLL {    private ProductDAO productDAO = new ProductDAO();    private List<Validator<Product>> validators;    public ProductBLL() {        validators = new ArrayList<>();        validators.add(new NrValidator());    }    public void add(Product product) {        try {            for (Validator<Product> validator : validators) {                validator.validate(product);            }            productDAO.insert(product, 1);        } catch (Exception e) {            JOptionPane.showMessageDialog(null, "Inserare produs nereusita");        }    }    public void delete(int id) {        try {            productDAO.delete(id);        } catch (Exception e) {            JOptionPane.showMessageDialog(null, "Stergere produs nereusita");        }    }    public void update(int id, Product product) {        try {            productDAO.updateProductById(id, product.getName(), product.getPrice(), product.getCurrentStock());        } catch (Exception e) {            JOptionPane.showMessageDialog(null, "Editare produs nereusita");        }    }    public Product find(int id) {        return productDAO.findById(id);    }    public JTable viewProducts() {        ArrayList<Product> products = productDAO.viewAll();        JTable table = productDAO.viewAll2(products);        return table;    }}